sap.ui.define(["sap/base/Log","sap/ui/base/Object"],(e,t)=>{"use strict";return t.extend("sap.ui.yesco.controller.ListSelector",{constructor:function(){this._oWhenListHasBeenSet=new Promise(e=>{this._fnResolveListHasBeenSet=e});this.oWhenListLoadingIsDone=new Promise((e,t)=>{this._oWhenListHasBeenSet.then(s=>{const i=s.getBinding("items");if(!i){return}i.attachEventOnce("dataReceived",()=>{if(this._oList.getItems().length){e({list:s})}else{t({list:s})}})})})},setBoundMasterList(e){this._oList=e;this._fnResolveListHasBeenSet(e)},selectAListItem(t){this.oWhenListLoadingIsDone.then(()=>{const e=this._oList;let s;if(e.getMode()==="None"){return}s=e.getSelectedItem();if(s&&s.getBindingContext().getPath()===t){return}e.getItems().some(s=>{if(s.getBindingContext()&&s.getBindingContext().getPath()===t){e.setSelectedItem(s);return true}})},()=>{e.warning("Could not select the list item with the path"+t+" because the list encountered an error or had no items")})},clearMasterListSelection(){this._oWhenListHasBeenSet.then(()=>{this._oList.removeSelections(true)})}})});